		PHALCON_INIT_NVAR(dst_x);
		ZVAL_LONG(dst_x, 0);

		PHALCON_INIT_NVAR(src_x);
		ZVAL_LONG(src_x, 0);

		PHALCON_INIT_NVAR(src_width);
		ZVAL_LONG(src_width, w);

		PHALCON_INIT_NVAR(src_height);
