	PHALCON_CALL_METHOD(compiler, this_ptr, "getcompiler");
	PHALCON_CALL_METHOD_PARAMS_1_NORETURN(compiler, "compile", template_path);
	
	PHALCON_INIT_VAR(compiled_template_path);
