	zval *partial_path, *params = NULL, *view_params, *new_params = NULL;
	zval *zfalse, *partials_dir, *real_path, *engines;

	PHALCON_MM_GROW();

	phalcon_fetch_params(1, 1, 1, &partial_path, &params);
	
