	PHALCON_INIT_VAR(connection);
	phalcon_call_method(connection, model, "getwriteconnection");
	
	PHALCON_OBS_VAR(meta_data);
	phalcon_read_property_this(&meta_data, this_ptr, SL("_metaData"), PH_NOISY_CC);
	
