			case PHQL_T_TRUE:
				array_init_size(return_value, 2);
				add_assoc_stringl_ex(return_value, ISS(type), SL("literal"), 1);
				add_assoc_stringl_ex(return_value, ISS(value), SL("TRUE"), 1);
