	assert(Z_LVAL_P(PHALCON_GLOBAL(z_zero)) == 0);
	assert(Z_LVAL_P(PHALCON_GLOBAL(z_one)) == 1);
#endif

	free(phalcon_globals->z_null);
	free(phalcon_globals->z_false);
